import {
  Backdrop,
  Badge,
  Box,
  CircularProgress,
  Container,
  CssBaseline,
  Fab,
  Fade,
  Tooltip,
  useScrollTrigger,
  Zoom,
} from "@material-ui/core";
import Brightness7Icon from "@material-ui/icons/Brightness7";
import AppBar from "@material-ui/core/AppBar";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import { makeStyles } from "@material-ui/core/styles";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import { ShoppingCart } from "@material-ui/icons";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import BookOutlinedIcon from "@material-ui/icons/BookOutlined";
import CheckCircleIcon from "@material-ui/icons/CheckCircle";
import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { NavLink, useHistory } from "react-router-dom";
import Login from "../../feature/Auth/component/Login";
import Register from "../../feature/Auth/component/Register";
import { logout } from "../../feature/Auth/useSlice";
import { hideMiniCart } from "../../feature/Cart/cartSlice";
import { cartItemCountSelector } from "../../feature/Cart/selectors";
import { toggleDarkMode } from "../../feature/System/systemSlice";
import SearchBar from "../SearchBar";
import Brightness4Icon from "@material-ui/icons/Brightness4";
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    zIndex: 999,
  },

  navBar: {
    zIndex: 999,
  },
  menuButton: {
    // marginRight: theme.spacing(2),
  },
  link: {
    textDecoration: "none",
    color: theme.palette.background.default,
    fontSize: 20,
    textTransform: "uppercase",
    margin: 5,
  },
  link_active: {
    color: "red",
    fontWeight: "bold",
  },
  menu: {
    display: "flex",
    alignItems: "center",
  },
  messageCart: {
    position: "absolute",
    padding: theme.spacing(2),
    backgroundColor: "rgb(255, 255, 255)",
    top: "90%",
    borderRadius: theme.spacing(1),
    right: theme.spacing(3),
    "&::before": {
      content: "''",
      position: "absolute",
      bottom: "100%",
      borderStyle: "solid",
      borderWidth: "8px",
      right: theme.spacing(2),
      borderColor: "transparent transparent rgb(255, 255, 255)",
    },
  },
  cartTitle: {
    marginLeft: theme.spacing(1),
    color: theme.palette.grey[700],
  },
  messageCartSub: {
    display: "flex",
    alignItems: "center",
    marginBottom: theme.spacing(2),
  },
  flexMenuRight: {
    flexWrap: "row nowrap",
  },
  backdrop: {
    zIndex: 998,
    color: "#fff",
  },
}));

export default function ButtonAppBar(props) {
  const history = useHistory();
  const dispatch = useDispatch();
  const MODE = { LOGIN: "login", REGISTER: "register" };
  const classes = useStyles();
  const userLogin = useSelector((state) => state.user);
  const isLogin = !!userLogin.current.id;
  const [openBackDrop, setOpenBackDrop] = React.useState(false);
  const handleCloseBackDrop = () => {
    setOpenBackDrop(false);
  };
  const handleToggle = (value) => {
    setOpenBackDrop(value);
  };
  const [open, setOpen] = useState(false);
  const [mode, setMode] = useState(MODE.LOGIN);
  const [anchorEl, setAnchorEl] = useState(null);
  const cartItemCount = useSelector(cartItemCountSelector);
  const isShowMessage = useSelector((state) => state.cart.isShowMiniCart);
  const isDarkMode = useSelector((state) => state.system.isDarkMode);
  const handleClick = (event) => setAnchorEl(event.currentTarget);

  const handleCloseMenu = () => {
    // setChecked((prev) => !prev);
    setAnchorEl(null);
  };
  const handleClickOpen = () => {
    setAnchorEl(null);
    setOpen(true);
  };

  const handleClose = () => setOpen(false);

  const handleLogoutAction = () => {
    const action = logout();
    dispatch(action);
    setAnchorEl(null);
    handleCloseMenu();
  };
  const handleCloseMiniCart = () => dispatch(hideMiniCart());

  const goToCart = () => {
    history.push("/cart");
  };

  console.log("Render");

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        className={classes.navBar}
        position="sticky"
        id="back-to-top-anchor"
      >
        <Container>
          <Toolbar>
            <Grid
              container
              direction="row"
              justify="flex-start"
              alignItems="center"
              xs={3}
              item
            >
              <Typography variant="h6">
                <NavLink
                  to="/products"
                  exact
                  className={classes.link}
                  // activeClassName={classes.link_active}
                >
                  SHOPING
                </NavLink>
              </Typography>
            </Grid>
            <Grid
              container
              direction="row"
              justify="center"
              alignItems="center"
              item
              xs={true}
            >
              <SearchBar onShowOverlay={handleToggle} />
            </Grid>
            <Grid
              wrap="nowrap"
              container
              direction="row"
              justify="flex-end"
              alignItems="center"
              xs={3}
            >
              {isLogin ? (
                <>
                  <Grid zeroMinWidth>
                    <Typography noWrap>{userLogin.current.fullName}</Typography>
                  </Grid>

                  <IconButton
                    className={classes.menuButton}
                    onClick={handleClick}
                    color="inherit"
                  >
                    <AccountCircleIcon />
                  </IconButton>
                </>
              ) : (
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={handleClickOpen}
                >
                  Đăng Nhập
                </Button>
              )}
              <Tooltip title="Giỏ hàng">
                <IconButton
                  aria-label="show cart"
                  color="inherit"
                  onClick={goToCart}
                >
                  <Badge badgeContent={cartItemCount} color="secondary">
                    <ShoppingCart />
                  </Badge>
                </IconButton>
              </Tooltip>
              {isShowMessage && (
                <Fade in={isShowMessage} timeout={500}>
                  <Box
                    boxShadow={1}
                    className={classes.messageCart}
                    onClick={handleCloseMiniCart}
                  >
                    <Box className={classes.messageCartSub}>
                      <CheckCircleIcon fontSize="small" color="primary" />
                      <Box
                        variant="body2"
                        align="center"
                        className={classes.cartTitle}
                      >
                        Thêm giỏ hàng thành công
                      </Box>
                    </Box>
                    <Button
                      onClick={goToCart}
                      variant="contained"
                      size="small"
                      color="primary"
                      fullWidth
                    >
                      Xem giỏ hàng
                    </Button>
                  </Box>
                </Fade>
              )}
              <Tooltip title="Dark Mode">
                <IconButton onClick={() => dispatch(toggleDarkMode())}>
                  {isDarkMode ? (
                    <Brightness7Icon />
                  ) : (
                    <Brightness4Icon style={{ color: "white" }} />
                  )}
                </IconButton>
              </Tooltip>
            </Grid>
          </Toolbar>
        </Container>
      </AppBar>
      {openBackDrop && (
        <Backdrop
          invisible={false}
          transitionDuration={300}
          className={classes.backdrop}
          open={openBackDrop}
          onClick={handleCloseBackDrop}
        >
          {}
        </Backdrop>
      )}
      <Menu
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleCloseMenu}
        anchorOrigin={{
          vertical: "bottom",
          horizontal: "right",
        }}
        transformOrigin={{
          vertical: "top",
          horizontal: "right",
        }}
        getContentAnchorEl={null}
      >
        <MenuItem onClick={handleCloseMenu}>Profile</MenuItem>
        <MenuItem onClick={handleCloseMenu}>My account</MenuItem>
        <MenuItem onClick={handleLogoutAction}>Logout</MenuItem>
      </Menu>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        {mode === MODE.LOGIN && (
          <>
            <Login handleClose={handleClose} />
            <Button color="primary" onClick={() => setMode(MODE.REGISTER)}>
              Don't you have account ? Sign up
            </Button>
          </>
        )}
        {mode === MODE.REGISTER && (
          <>
            <Register handleClose={handleClose} />
            <Button color="primary" onClick={() => setMode(MODE.LOGIN)}>
              Do you have account ? Sign in
            </Button>
          </>
        )}
      </Dialog>
    </div>
  );
}
